mapper.select:
SELECT PNO, SNO, PNAME, SELTAG, RURL, PQUANTITY, ISCRAFT, PRICE, PDETAIL, DELFLAG
FROM PRODUCT
WHERE SNO = #{sNo}

SELECT PNO, SNO, PNAME, SELTAG, RURL, PQUANTITY, ISCRAFT, PRICE, PDETAIL, DELFLAG
FROM PRODUCT
WHERE PNO = #{pNo}

mapper.update:
UPDATE PRODUCT 
SET PNAME = #{pName}, SELTAG = #{selTag}, RURL = #{rUrl}, PQUANTITY =  #{pQuantity}, ISCRAFT = #{isCraft}, PRICE = #{price}, PDETAIL = #{pDetail}
WHERE PNO = #{pNo}
